//文件对象
//云端代码只支持文件的删除操作。删除文件，必须要知道文件的组名和url.

var __Request = require("./../bmob-cloud-common").Request;
var assert = require("assert");

module.exports = (function () {
    var model = {};

    /**
     function onRequest(request, response, modules) {

     var file = modules.oFile;

     file.delete({
     "group":"group1",
     "url":"M00/00/01/wKgBP1N3FAWRJXsSAAAB_rYZATs52.html"
     },function(err,data){
     //回调函数
     });
     }
     */
    model.delete = function (request, callback) {
        assert.ifError(request.group == null);
        assert.ifError(request.url == null);

        __Request("files", null, null, 'DELETE', request, callback);
    }

    return model;
})();